const W = 64;
const l = 6;
const rounds = 24;
const b = 1600;

module.exports = function permute(state) {
    let A = StateToA(state);

    for (let round = 0; round < rounds; round++) {
        A = theta(A);
        A = rho(A);
        A = pi(A);
        A = chi(A);
        A = iota(A, round);
    }
    return AToState(A);
}

function theta(A) {

    const C = new Array(5).fill(0).map(() => new Array(W).fill(0));
    const D = new Array(5).fill(0).map(() => new Array(W).fill(0));
    const B = Array.from({length: 5},
        () => Array.from({length: 5},
            () => new Array(W).fill(0)));

    for (let i = 0; i < 5; i++)
        for (let k = 0; k < W; k++)
            C[i][k] = A[i][0][k] ^ A[i][1][k] ^ A[i][2][k] ^ A[i][3][k] ^ A[i][4][k];

    for (let i = 0; i < 5; i++)
        for (let k = 0; k < W; k++)
            D[i][k] = C[Math.abs((i - 1) % 5)][k] ^ C[(i + 1) % 5][Math.abs((k - 1) % W)];

    for (let i = 0; i < 5; i++)
        for (let j = 0; j < 5; j++)
            for (let k = 0; k < W; k++)
                B[i][j][k] = A[i][j][k] ^ D[i][k];

    return B;
}

function rho(A) {
    const B = Array.from({length: 5},
        () => Array.from({length: 5},
            () => new Array(W).fill(0)));

    for (let k = 0; k < W; k++)
        B[0][0][k] = A[0][0][k];

    let i = 1, j = 0;
    for (let t = 0; t < 24; t++) {
        console.log('t', t)
        for (let k = 0; k < W; k++) {
            B[i][j][k] = A[i][j][Math.abs((k - (t + 1) * (t + 2) / 2) % W)];
        }
        let i_tmp = j;
        let j_tmp = (2 * i + 3 * j) % 5;
        i = i_tmp;
        j = j_tmp;
        // console.log('i', i);
        // console.log('j', j);
    }

    return B;

}

function pi(A) {

    const B = Array.from({length: 5},
        () => Array.from({length: 5},
            () => new Array(W).fill(0)));

    for (let i = 0; i < 5; i++)
        for (let j = 0; j < 5; j++)
            for (let k = 0; k < W; k++)
                B[i][j][k] = A[(i + 3 * j) % 5][i][k]

    return B;

}

function chi(A) {

    const B = Array.from({length: 5},
        () => Array.from({length: 5},
            () => new Array(W).fill(0)));

    for (let i = 0; i < 5; i++)
        for (let j = 0; j < 5; j++)
            for (let k = 0; k < W; k++) {
                // console.log('test', !A[(i + 1) % 5][j][k])
                // B[i][j][k] = A[i][j][k] ^ ((!A[(i + 1) % 5][j][k] && A[(i + 2) % 5][j][k]));
                B[i][j][k] = A[i][j][k] ^ ((A[(i + 1) % 5][j][k] ^ 1) && A[(i + 2) % 5][j][k]);
            }

    return B;
}

function iota(A, round) {

    const B = Array.from({length: 5},
        () => Array.from({length: 5},
            () => new Array(W).fill(0)));

    for (let i = 0; i < 5; i++)
        for (let j = 0; j < 5; j++)
            for (let k = 0; k < W; k++)
                B[i][j][k] = A[i][j][k];

    const RC = new Array(W).fill(0);
    // for (let i = 0; i <= l; i++)?????????
    for (let i = 0; i < l; i++)
        RC[Math.pow(2, i) - 1] = rc(i + 7 * round);

    for (let k = 0; k < W; k++)
        B[0][0][k] = B[0][0][k] ^ RC[k];

    return B;

}

function rc(t) {
    // if (!(t % 255))
    if (t % 255 === 0)
        return 1;

    let R = [1, 0, 0, 0, 0, 0, 0, 0];

    // < OR <=?????
    let d = 0
    for (let i = 1; i <= t % 255; i++) {
        if (d < 3) console.log('\n-----------')
        R.unshift(0);
        if (d < 3) console.log(R)
        R[0] = R[0] ^ R[8];
        if (d < 3) console.log(R)
        R[4] = R[4] ^ R[8];
        if (d < 3) console.log(R)
        R[5] = R[5] ^ R[8];
        if (d < 3) console.log(R)
        R[6] = R[6] ^ R[8];
        if (d < 3) console.log(R)
        R = R.slice(0, 8);
        if (d < 3) console.log(R)
        d += 3;
    }
    return R[0];
}

function StateToA(state) {
    const A = Array.from({length: 5},
        () => Array.from({length: 5},
            () => new Array(W).fill(0)));

    for (let i = 0; i < 5; i++)
        for (let j = 0; j < 5; j++)
            for (let k = 0; k < W; k++)
                A[i][j][k] = state[(5 * i + j) * W + k];

    // console.log(A)
    return A;
}

function AToState(A) {
    let state = new Array(b).fill(0);
    let ind = 0;
    for (let i = 0; i < 5; i++)
        for (let j = 0; j < 5; j++)
            for (let k = 0; k < W; k++) {
                state[ind] = A[i][j][k];
                ind++;
            }
    return state;
}
